import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { TYPES } from 'src/components/data'
import Input from '@material-ui/core/Input';
import Slider from '@material-ui/core/Slider';

const useStyles = makeStyles(theme => ({
    root: {
        // width: '100%',
        // marginTop: theme.spacing(3),
        // overflowX: 'auto',
    },
    table: {
        // width: 600,
    },
    tableTitle: {
        fontSize: 20
    },
    TableCell: {

    }
}));


export default function SimpleTable(props) {
    const { tableTitle, data } = props
    const classes = useStyles();
    const [values, setValues] = React.useState({});

    const handleChange = () => event => {
        setValues({ ...values, [event.target.id]: event.target.value });
    };
    function valuetext(value) {
        return `${value}°C`;
    }
    const marks = [
        {
            value: 0,
            label: '0°C',
        },
        {
            value: 20,
            // label: '20°C',
        },
        {
            value: 37,
            // label: '37°C',
        },
        {
            value: 100,
            label: '100°C',
        },
    ];
    console.log('values', values)
    const renderValueType = (row) => {
        switch (row.type) {
            case TYPES.NUMERIC:
                return <Input
                    id={row.location}
                    label="Number"
                    value={values.age}
                    onChange={handleChange()}
                    type="tel"
                    className={classes.textField}

                />
            case TYPES.RANGE:
                return <Slider
                    defaultValue={80}
                    getAriaValueText={valuetext}
                    aria-labelledby="discrete-slider-always"
                    step={row.ticks}
                    min={row.min}
                    max={row.max}
                    marks={marks}
                    valueLabelDisplay="on"
                />
            case TYPES.SELECT:
                return;


            default:
                return row.type
        }
    }

    const renderTableData = () => {
        return data.map(row => (
            <TableRow key={row.name}>
                <TableCell align="left" className={classes.TableCell}>
                    {row.name}
                </TableCell>
                <TableCell align="left">{row.value}</TableCell>
                <TableCell align="left">{renderValueType(row)}</TableCell>
            </TableRow>
        ))
    }
    console.log('data', data);
    if (!data) return <div>no data</div>
    return (
        <Paper className={classes.root}>
            <Table className={classes.table}>
                <TableHead>
                    <TableRow>
                        <TableCell className={classes.tableTitle} align="left">{tableTitle}</TableCell>
                        <TableCell align="left">Value</TableCell>
                        <TableCell align="left">Units</TableCell>

                    </TableRow>
                </TableHead>
                <TableBody>
                    {renderTableData()}
                </TableBody>
            </Table>
        </Paper>
    );
}
