import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { TYPES } from 'src/components/data'
import Input from '@material-ui/core/Input';
import Slider from '@material-ui/core/Slider';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles(theme => ({
    root: {
        // width: '100%',
        // marginTop: theme.spacing(3),
        // overflowX: 'auto',
    },
    table: {
        // width: 600,
    },
    tableTitle: {
        fontSize: 20
    },
    TableCell: {

    }
}));


export default function SimpleTable(props) {
    const { tableTitle, data } = props
    const classes = useStyles();

    const [values, setValues] = React.useState({});

    const handleTextChange = () => event => {
        setValues({ ...values, [event.target.id]: event.target.value });
    };
    function valuetext(value) {
        return `${value}`;
    }

    const handleSelectChange = event => { //continue here
        setValues({ ...values, [event.target.id]: event.target.value });
    };
    const onChangeSlider = event => { //continue here
        console.log('event', event);

        setValues({ ...values, [event.target.id]: event.target.value });
    };

    const marks = [
        {
            value: 0,
            label: '0째C',
        },
        {
            value: 20,
            // label: '20째C',
        },
        {
            value: 37,
            // label: '37째C',
        },
        {
            value: 100,
            label: '100째C',
        },
    ];
    console.log('values', values)

    const getMarks = () => {
        let arr = []
        for (let index = data.min; index <= data.max; index + data.ticks) {
            arr.push(index)
        }
        return arr
    }
    const renderValueType = (row) => {
        switch (row.type) {
            case TYPES.NUMERIC:
                return <Input
                    id={row.location}
                    label="Number"
                    value={values[row.location]}
                    onChange={handleTextChange()}
                    type="tel"
                    className={classes.textField}

                />
            case TYPES.RANGE:
                return <Slider
                    id={row.location}
                    defaultValue={row.max / 2}
                    getAriaValueText={valuetext}
                    onChange={onChangeSlider}
                    aria-labelledby="discrete-slider-always"
                    step={row.ticks}
                    min={row.min}
                    max={row.max}
                    marks={getMarks()}
                    valueLabelDisplay="on"
                />
            case TYPES.SELECT:
                return <Select
                    id={row.location}
                    native
                    value={values[row.location]}
                    onChange={handleSelectChange}
                // inputProps={{
                //     name: 'age',
                //     // id: 'age-native-simple',
                // }}
                >
                    {row.data.map(item => {
                        return <option value={item}>{item}</option>
                    })}
                    {/* <option value="" />
                    <option value={item}>{item}</option>
                    <option value={item}>{item}</option> */}
                </Select>


            default:
                return row.type
        }
    }

    const renderTableData = () => {
        return data.map(row => (
            <TableRow key={row.name}>
                <TableCell align="left" className={classes.TableCell}>
                    {row.name}
                </TableCell>
                <TableCell align="left">{row.value}</TableCell>
                <TableCell align="left">{renderValueType(row)}</TableCell>
            </TableRow>
        ))
    }
    console.log('data', data);
    if (!data) return <div>no data</div>
    return (
        <Paper className={classes.root}>
            <Table className={classes.table}>
                <TableHead>
                    <TableRow>
                        <TableCell className={classes.tableTitle} align="left">{tableTitle}</TableCell>
                        <TableCell align="left">Value</TableCell>
                        <TableCell align="left">Units</TableCell>

                    </TableRow>
                </TableHead>
                <TableBody>
                    {renderTableData()}
                </TableBody>
            </Table>
        </Paper>
    );
}
